var utils = require("../index.js");
var timestamp = utils.timestamp;
exports.timeStampWorks = function(test){
    test.expect(2);
    var dt =timestamp();
    var dt2 = timestamp(new Date(2012,1,1));
    test.ok(dt, "can give timestamp");
    test.notEqual(dt,dt2,"timestamps must be not equal");
    test.done();
};
var uuid = utils.uuid;
exports.canCreateUid = function(test){
    test.expect(2);
    var g1 = uuid();
    var g2 = uuid();
    test.ok(g1 && g2,"guids was not created");
    test.notEqual(g1,g2,"guids mus be unique");
    test.done();
}

